mapper.update:
UPDATE REQUEST SET
PHOTO_DIR=#{photo_dir},
REQ_TITLE=#{req_title}, REQ_CONTENT=#{req_content}, PREF_DATE=#{pref_date}, UPDDATE=SYSDATE, UPDATER_SEQ=#{updater_seq} WHERE REQ_SEQ=#{req_seq}

mapper.select:
SELECT R.REQ_SEQ, R.REQ_TITLE, R.REQ_CONTENT, NVL((SELECT COUNT(1) FROM DEAL WHERE DEAL_STATE=0 GROUP BY REQ_SEQ HAVING REQ_SEQ=R.REQ_SEQ),0) AS BIDS, P.SIDO_NAME, P.SGG_NAME, P.X_POS, P.Y_POS, P.ADDR FROM REQUEST R, PIANO P WHERE R.PIANO_SEQ=P.PIANO_SEQ
AND P.SGG_CODE LIKE #{sgg_code}
AND R.PRIVATE_SEQ IS NULL AND R.REQ_STATE=0

mapper.update:
UPDATE REQUEST
	SET REQ_STATE=2
WHERE
	REQ_SEQ=#{0}
	AND PRIVATE_SEQ=#{1}
